library(dendextend)library(dplyr)library(DECIPHER)library(ggplot2)library(ggtree)library(phangorn)library(corrplot)tree1 <- read.tree('/Users/mexico_mp_extract_all_with_mex_with_id_alig.tree')tree1 <- midpoint(tree1)tree2 <- read.tree('/Users/mexico_h3_extract_all_with_mex_with_id.tree')tree2 <- midpoint(tree1)tree1 <- compute.brlen(tree1)tree2 <- compute.brlen(tree2)tree1 <- as.dendrogram(tree1)tree2 <- as.dendrogram(tree2)dndlist <- dendextend::dendlist(tree1, tree2)dendextend::tanglegram(dndlist, fast = TRUE, margin_inner = 5)dendextend::tanglegram(dndlist, fast = TRUE, margin_inner = 5)dndlist %>% tanglegram(common_subtrees_color_branches = TRUE,                      lab.cex = .5, margin_inner = 1.3)dndlist %>% tanglegram(common_subtrees_color_branches = F,                      lab.cex = .5, margin_inner = 0.3)dendlist(highlight_branches(tree1), highlight_branches(tree2))tanglegram(dndlist, sort = TRUE, common_subtrees_color_lines = TRUE, highlight_distinct_edges = FALSE)tangle<-dendextend::untangle(dndlist, "step2side")#tangle2<-dendextend::untangle(dndlist, "DendSer")dendextend::entanglement(tangle)#dendextend::entanglement(tangle2)# Dibuja el tanglegram con las ramas coloreadasa <- dendextend::tanglegram(  tangle,  margin_inner = 5,  edge.lwd = 1,  lwd = 1,  lab.cex = 0.8,  columns_width = c(5, 2, 5),  common_subtrees_color_lines = TRUE)